}; int main() { Point p1(1,1); Point p2(3,3); Point POINT(10,10); auto fn = std::bind(&Point::add, &POINT, std::placeholders::_1); fn(p1); std::cout << POINT.x_ << ", " << POINT.y_ << "\n"; //11, 11 fn(p2); std::cout << POINT.x_ << ", " << POINT.y_ << "\n"; //14, 14
