[...params.keys()] // result: ['key', 'v', 'g', 'k', 'qe']
[...params.values()] // result: ['1', '2', '25', '40', 'hello']
[...params.entries()] /* result: [ ["key","1"], ["v","2"], ["g","25"], ["k","40"], ["qe","hello"] ]
*/ /* 設值器 */
params.append('test', 'string');
